CREATE TABLE Currency( 
CCY CHAR(3) PRIMARY KEY
)

CREATE TABLE IterestRate( 
Currency CHAR(3) REFERENCES Currency(CCY),
Date DATE,
Maturity VARCHAR(3),
Rate FLOAT
)

CREATE TABLE SecurityList(
ISIN VARCHAR(11) PRIMARY KEY,
Currency CHAR(3) REFERENCES Currency(CCY),
Country CHAR(3),
Exchange CHAR(3)
)

CREATE TABLE ClosePrice(
ISIN VARCHAR(11) REFERENCES SecurityList(ISIN),
CCY CHAR(3),
Date DATE,
Price FLOAT
)

